// typings.d.ts
declare module '*.html' {
    const content: string;
    export default content;
}
declare module "*.json" {
    const content: any;
    export default content;
}
declare module "*.md" {
    const content: any;
    export default content;
}
declare module "*.htm" {
    const content: any;
    export default content;
}
declare module "*.svg" {
    const content: string;
    export default content;
}